import React from "react";

const DiaryItem = ({ id, emotion, content, date, img }) => {
  const strDate = new Date(parseInt(date));
  const dateText = `${strDate.getFullYear()}년 ${
    strDate.getMonth() + 1
  }월 ${strDate.getDate()}일`;
  const dayArr = [
    "일요일",
    "월요일",
    "화요일",
    "수요일",
    "목요일",
    "금요일",
    "토요일",
  ];
  return (
    <div className="DiaryItem">
      <div
        className={[
          "emotion_img_wrapper",
          `emotion_img_wrapper_${emotion}`,
        ].join(" ")}
      >
        <img
          className={`img_${emotion}`}
          src={process.env.PUBLIC_URL + `assets/emotion${emotion}.png`}
        />
      </div>
      <div className="info_wrapper">
        <div className="diary_date">{dateText}</div>
        <div className="diary_conent_preview"></div>
      </div>
      <div className=""></div>
    </div>
  );
};

export default DiaryItem;
