import React, { useContext, useRef, useState } from "react";
import MyHeader from "./MyHeader";
import MyButton from "./MyButton";
import { useNavigate } from "react-router-dom";
import EmotionItem from "./EmotionItem";
import { emotionList } from "../util/emotionList";
import { DiaryDispatchContext } from "../App";

const getStringDate = (date) => {
  return date.toISOString().slice(0, 10);
};

const DiaryEditor = ({ isEdit, originData }) => {
  const navigate = useNavigate();

  const contentRef = useRef();
  const fileRef = useRef();

  const [img, setImg] = useState();
  const [emotion, setEmotion] = useState(3);
  const [date, setDate] = useState(getStringDate(new Date()));
  const [content, setContent] = useState("");
  const { onCreate } = useContext(DiaryDispatchContext);

  const handleClickEmotion = (emotion) => {
    setEmotion(emotion);
  };

  const imgUpload = () => {
    fileRef.current.click();
  };

  const handleImgChange = (e) => {
    console.log(e.target);
    const imgFile = e.target.files[0];
    const fileReader = new FileReader();
    fileReader.readAsDataURL(imgFile);
    fileReader.onload = () => {
      const newImg = fileReader.result;
      setImg(newImg);
    };
  };

  const handlerSubmit = () => {
    if (content.length < 1) {
      contentRef.current.focus();
      return;
    }

    onCreate(date, content, emotion, img);
    navigate("/", { replace: true });
  };

  return (
    <div className="DiaryEditor">
      <div>
        <MyHeader
          headerText={ isEdit ? "ÏÉà ÏùºÍ∏∞ Ïì∞Í∏∞" : "ÏàòÏ†ïÌïòÍ∏∞"}
          leftChild={
            <MyButton text={"< Îí§Î°ú Í∞ÄÍ∏∞"} onClick={() => navigate(-1)} />
          }
        />
        <div>
          <section className="edit_time_flex">
            <h3>üìÖ TODAY ? </h3>
            <div className="input_box">
              <input
                value={date}
                className="input_date"
                type="date"
                onChange={(e) => setDate(e.target.value)}
              />
            </div>
          </section>
          <section>
            <h3> Ïò§ÎäòÏùò Í∞êÏ†ïÏùÄ ?</h3>
            <div className="input_box emotion_list_wrapper">
              {emotionList.map((it) => (
                <EmotionItem
                  key={it.emotion_id}
                  {...it}
                  onClick={handleClickEmotion}
                  isSelected={it.emotion_id === emotion}
                />
              ))}
            </div>
          </section>
          <section className="img_input_wrapper">
            <h3> üìù Ïò§ÎäòÏùò ÏùºÍ∏∞</h3>
            <MyButton
              text={"üì∑ Ïù¥ÎØ∏ÏßÄ Îã¥Í∏∞"}
              type={"positive"}
              onClick={imgUpload}
            />
            <input
              ref={fileRef}
              type="file"
              accept="image/*"
              onChange={handleImgChange}
            />
          </section>
          <section>
            <div className="input_box text_wrapper">
              <img src={img} className="thumbnail" />
              <textarea
                placeholder="Ïò§Îäò ÌïòÎ£®Îäî Ïñ¥Îï†ÎÇòÏöî?"
                ref={contentRef}
                value={content}
                onChange={(e) => setContent(e.target.value)}
              />
            </div>
          </section>

          <section>
            <div className="control_box">
              <MyButton text={"Ï∑®ÏÜåÌïòÍ∏∞"} onClick={() => navigate(-1)} />
              <MyButton
                text={"ÏûëÏÑ±ÏôÑÎ£å"}
                onClick={() => {
                  handlerSubmit();
                }}
                type={"positive"}
              />
            </div>
          </section>
        </div>
      </div>
    </div>
  );
};

export default DiaryEditor;
